input = ""

pole = list(input)
count = 0

while pole:
    count += 1
    forteen = pole[:14]

    for letter in forteen:
        if forteen.count(letter) > 1:
            del pole[0]
            break
    else:
        print("Characters processed before the first start-of-message marker:", count + 13)
